import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person'
import Radium from 'radium'


class App extends Component {

  state = {
    persons : [
      {id: '1', name:'Rad', age:'55'},
      {id: '2', name:'Tom', age:'22'},
      {id: '3', name:'John', age:'34'}
    ],
    otherState: 'this is something other',

    showPersons: false
  }

  deletePersonHandler = (personIndex) => {
    // const persons = this.state.persons.slice();
    const persons = [...this.state.persons]
    persons.splice(personIndex, 1);
    this.setState({persons: persons});
  }

  switchNameHandler = (newName) => {
    // console.log('was clicked');
    // this.state.persons[0].name="Radoslaw";
    this.setState(
      {
        persons: [
          {name: newName, age:'55'},
          {name:'Tom2', age:'22'},
          {name:'John', age:'34'}
        ]
      }
    )
  }

  nameChangeHandler = (event, id) => {
    const personIndex = this.state.persons.findIndex(pers => {
      return pers.id ===id;
    });
    const person = {
      ...this.state.persons[personIndex]
    }
    person.name = event.target.value;

    const persons = [...this.state.perosns]
    persons[personIndex] = person;

    this.setState({persons: persons})
  }

  togglePersonHandler = () => {
    const doesShow = this.state.showPersons;
    this.setState({showPersons: !doesShow});
  }

  render() {

    const style={
      backgroundColor: 'green',
      color: 'white',
      font: 'ingerit',
      border: '1px solid blue',
      padding: '8px',
      cursor: 'pointer',
      ':hover':{
        backgroundColor: 'lightgree',
        color: 'black'
      }

    };

    let persons = null;
    if (this.state.showPersons) {
      persons = (
        <div >         
          {this.state.persons.map((person, index) => {
            return<Person 
              name={person.name} 
              age ={person.name}
              click={ () => this.deletePersonHandler(index)}
              key={person.id}
              change={ (event) => this.nameChangeHandler(event, person.id)}/>
          })}

        </div> 
      )
      style.backgroundColor='red';
      style[':hover'] = {
        backgroundColor: 'lightgred',
        color: 'black'
      };
    }

    let classes =[];

    if(this.state.persons.length <=2 ){
      classes.push('red');
    }
    if(this.state.persons.length <=1 ){
      classes.push('bold');
    }
    return (
      <div className="App">
        <h1> Hi, I am first react App......</h1>
        <p className={classes.join(' ')}> this is really working!</p>
        <button 
        style={style}
        onClick={this.togglePersonHandler} >toogle persons</button>
        {persons}
      </div>
    )
    // return React.createElement('div', null, React.createElement('h1', {className: "App"}, 'that this work now'));
  }
}

export default Radium(App);
