import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person'


class App extends Component {

  state = {
    persons : [
      {name:'Rad', age:'55'},
      {name:'Tom', age:'22'},
      {name:'John', age:'34'}
    ],
    otherState: 'this is something other',

    showPersons: false
  }

  deletePersonHandler = (personIndex) => {
    const persons = this.state.persons;
    persons.splice(personIndex, 1);
    this.setState({persons: persons});
  }

  switchNameHandler = (newName) => {
    // console.log('was clicked');
    // this.state.persons[0].name="Radoslaw";
    this.setState(
      {
        persons: [
          {name: newName, age:'55'},
          {name:'Tom2', age:'22'},
          {name:'John', age:'34'}
        ]
      }
    )
  }

  nameChangeHandler = (event) => {
    this.setState({
      persons: [
        {name: event.target.value, age:'55'},
        {name: 'test', age:'22'},
        {name:'John', age:'34'}
      ]
    }
    )
  }

  togglePersonHandler = () => {
    const doesShow = this.state.showPersons;
    this.setState({showPersons: !doesShow});
  }

  render() {

    const style={
      backgroundColor: 'white',
      font: 'ingerit',
      border: '1px solid blue',
      padding: '8px',
      cursor: 'pointer'
    };

    let persons = null;
    if (this.state.showPersons) {
      persons = (
        <div >         
          {this.state.persons.map((person, index) => {
            return<Person 
              name={person.name} 
              age ={person.name}
              click={this.deletePersonHandler(index)}/>
          })}
          {/* <Person 
          name={this.state.persons[0].name} 
          age={this.state.persons[0].age} 
          click={this.switchNameHandler.bind(this, 'Radoslaw!!!!!!')}
          changed={this.nameChangeHandler}/>
          <Person 
          name={this.state.persons[1].name}  
          age={this.state.persons[1].age} />
          <Person 
          name={this.state.persons[2].name}  
          age={this.state.persons[0].age} > My Hbbies: Recing</Person> */}
        </div> 
      )
    }

    return (
      <div className="App">
        <h1> Hi, I am first react App......</h1>
        <p> this is really working!</p>
        <button 
        style={style}
        onClick={this.togglePersonHandler} >toogle persons</button>
        {persons}
      </div>
    )
    // return React.createElement('div', null, React.createElement('h1', {className: "App"}, 'that this work now'));
  }
}

export default App;
