import React from 'react'
import myCss from './Cockpit.module.css';

const cockpit = (props) => {


    let btnClass = '';
    if (props.showPersons) {
        btnClass = myCss.Red;
    }
    const assignClasses = [];
    if ( props.state.persons.length <= 2 ) {
      assignClasses.push( myCss.red ); // classes = ['red']
    }
    if ( props.state.persons.length <= 1 ) {
      assignClasses.push( myCss.bold ); // classes = ['red', 'bold']
    }

    return (
        <div className={myCss.Cockpit}>
            <h1>Hi, I'm a React App</h1>
            <p className={assignClasses.join( ' ' )}>This is really working!</p>
            <button className={btnClass}
            onClick={this.togglePersonsHandler}>Toggle Persons</button>
        </div>
    )
}

export default cockpit;
