import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person'
import './Person.css';


class App extends Component {

  state = {
    persons : [
      {name:'Rad', age:'55'},
      {name:'Tom', age:'22'},
      {name:'John', age:'34'}
    ],
    otherState: 'this is something other'
  }

  switchNameHandler = (newName) => {
    // console.log('was clicked');
    // this.state.persons[0].name="Radoslaw";
    this.setState({
      persons: [
        {name: newName, age:'55'},
        {name:'Tom2', age:'22'},
        {name:'John', age:'34'}
      ]
    }
    )
  }

  nameChangeHandler = (event) => {
    this.setState({
      persons: [
        {name: event.target.value, age:'55'},
        {name: 'test', age:'22'},
        {name:'John', age:'34'}
      ]
    }
    )
  }


  render() {
    return (
      <div className="App">
        <h1> Hi, I am first react App......</h1>
        <p> this is really working!</p>
        <button onClick={()=>this.switchNameHandler('radoslaw!!')} >Switch Name</button>
        <Person 
        name={this.state.persons[0].name} 
        age={this.state.persons[0].age} 
        click={this.switchNameHandler.bind(this, 'Radoslaw!!!!!!')}
        changed={this.nameChangeHandler}/>
        <Person 
        name={this.state.persons[1].name}  
        age={this.state.persons[1].age} />
        <Person 
        name={this.state.persons[2].name}  
        age={this.state.persons[0].age} > My Hbbies: Recing</Person>
      </div>
    )
    // return React.createElement('div', null, React.createElement('h1', {className: "App"}, 'that this work now'));
  }
}

export default App;
